
public class CastingExTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int n = 0;
		double doubleNum = 43.543;
		n = (int)doubleNum;
		doubleNum = n;
		
		System.out.println("n: " + n);
		System.out.println("doubleNum: " + doubleNum);
		
//		실수의 기본형은 0.0 
		
		
	}

}
